import React, { Component } from 'react';
// import { Map, GoogleApiWrapper, InfoWindow } from 'google-maps-react';

const API_KEY = 'wiTFWgwmrifsUsqR6';//'AIzaSyB_dgZnBaGT1Cc5n_tmpKVoO_MMGmv1TrA';

const loadJS = (src) => {
  const ref = window.document.getElementsByTagName('script')[0];
  const script = window.document.createElement('script');
  script.src = src;
  script.async = true;
  ref.parentNode.insertBefore(script, ref);
};

export default class MapContainer extends React.Component {
  constructor(props) {
    super(props);
    this.mapRef = React.createRef();
    this.initMap = this.initMap.bind(this);
  }

  componentDidMount() {
    if (window.google) {
      // script has been loaded already
      this.initMap();
    } else {
      window.initMap = this.initMap;
       loadJS(`https://maps.googleapis.com/maps/api/js?key=${API_KEY}&callback=initMap`);
    //  loadJS(`https://goo.gl/maps/wiTFWgwmrifsUsqR6`);
    }
  }

  initMap() {
    const g = window.google;
    const map = new g.maps.Map(this.mapRef.current, {
      zoom: 17,
      center: {
        lat: 45.161712,lng: 11.4518353
      },
    });
    /* draw markers or polygon here */
  }

  render() {
    return (
      <div style={{ width: 500, height: 500 }} ref={this.mapRef}>
        {/* map rendered here */}
      </div>
    );
  }
}
